var mkPlayer={api:"api.php",loadcount:20,method:"POST",defaultlist:3,autoplay:false,coverbg:true,mcoverbg:true,dotshine:true,mdotshine:false,volume:0.6,version:"v2.41",debug:false};var rem=[];function audioErr(){if(rem.playlist===undefined)return true;if(rem.errCount>10){layer.msg('似乎出了点问题~播放已停止');rem.errCount=0}else{rem.errCount++;layer.msg('当前歌曲播放失败，自动播放下一首');nextMusic()}}function pause(){if(rem.paused===false){rem.audio[0].pause()}else{if(rem.playlist===undefined){rem.playlist=rem.dislist;musicList[1].item=musicList[rem.playlist].item;playerSavedata('playing',musicList[1].item);listClick(0)}rem.audio[0].play()}}function orderChange(){var a=$(".btn-order");a.removeClass();switch(rem.order){case 1:a.addClass("player-btn btn-order btn-order-list");a.attr("title","列表循环");layer.msg("列表循环");rem.order=2;break;case 3:a.addClass("player-btn btn-order btn-order-single");a.attr("title","单曲循环");layer.msg("单曲循环");rem.order=1;break;default:a.addClass("player-btn btn-order btn-order-random");a.attr("title","随机播放");layer.msg("随机播放");rem.order=3}}function audioPlay(){rem.paused=false;refreshList();$(".btn-play").addClass("btn-state-paused");if((mkPlayer.dotshine===true&&!rem.isMobile)||(mkPlayer.mdotshine===true&&rem.isMobile)){$("#music-progress .mkpgb-dot").addClass("dot-move")}var a=musicList[rem.playlist].item[rem.playid];var b=" 正在播放: "+a.name+" - "+a.artist;if(rem.titflash!==undefined){clearInterval(rem.titflash)}titleFlash(b)}function titleFlash(a){var b=function(){a=a.substring(1,a.length)+a.substring(0,1);document.title=a};rem.titflash=setInterval(function(){b()},300)}function audioPause(){rem.paused=true;$(".list-playing").removeClass("list-playing");$(".btn-play").removeClass("btn-state-paused");$("#music-progress .dot-move").removeClass("dot-move");if(rem.titflash!==undefined){clearInterval(rem.titflash)}document.title=rem.webTitle}function prevMusic(){playList(rem.playid-1)}function nextMusic(){switch(rem.order?rem.order:1){case 1,2:playList(rem.playid+1);break;case 3:if(musicList[1]&&musicList[1].item.length){var a=parseInt(Math.random()*musicList[1].item.length);playList(a)}break;default:playList(rem.playid+1);break}}function autoNextMusic(){if(rem.order&&rem.order===1){playList(rem.playid)}else{nextMusic()}}function updateProgress(){if(rem.paused!==false)return true;music_bar.goto(rem.audio[0].currentTime/rem.audio[0].duration);scrollLyric(rem.audio[0].currentTime)}function listClick(a){var b=a;if(mkPlayer.debug){console.log("点播了列表中的第 "+(a+1)+" 首歌 "+musicList[rem.dislist].item[a].name)}if(rem.dislist===0){if(rem.playlist===undefined){rem.playlist=1;rem.playid=musicList[1].item.length-1}var c=musicList[0].item[a];for(var i=0;i<musicList[1].item.length;i++){if(musicList[1].item[i].id==c.id&&musicList[1].item[i].source==c.source){b=i;playList(b);return true}}musicList[1].item.splice(rem.playid+1,0,c);b=rem.playid+1;playerSavedata('playing',musicList[1].item)}else{if((rem.dislist!==rem.playlist&&rem.dislist!==1)||rem.playlist===undefined){rem.playlist=rem.dislist;musicList[1].item=musicList[rem.playlist].item;playerSavedata('playing',musicList[1].item);refreshSheet()}}playList(b);return true}function playList(a){if(rem.playlist===undefined){pause();return true}if(musicList[1].item.length<=0)return true;if(a>=musicList[1].item.length)a=0;if(a<0)a=musicList[1].item.length-1;rem.playid=a;if(musicList[1].item[a].url===null||musicList[1].item[a].url===""){ajaxUrl(musicList[1].item[a],play)}else{play(musicList[1].item[a])}}function initAudio(){rem.audio=$('<audio></audio>').appendTo('body');rem.audio[0].volume=volume_bar.percent;rem.audio[0].addEventListener('timeupdate',updateProgress);rem.audio[0].addEventListener('play',audioPlay);rem.audio[0].addEventListener('pause',audioPause);$(rem.audio[0]).on('ended',autoNextMusic);rem.audio[0].addEventListener('error',audioErr)}function play(a){if(mkPlayer.debug){console.log('开始播放 - '+a.name);console.info('id: "'+a.id+'",\n'+'name: "'+a.name+'",\n'+'artist: "'+a.artist+'",\n'+'album: "'+a.album+'",\n'+'source: "'+a.source+'",\n'+'url_id: "'+a.url_id+'",\n'+'pic_id: "'+a.pic_id+'",\n'+'lyric_id: "'+a.lyric_id+'",\n'+'pic: "'+a.pic+'",\n'+'url: "'+a.url+'"')}if(a.url=="err"){audioErr();return false}addHis(a);if(rem.dislist==2&&rem.playlist!==2){loadList(2)}else{refreshList()}try{rem.audio[0].pause();rem.audio.attr('src',a.url);rem.audio[0].play()}catch(e){audioErr();return}rem.errCount=0;music_bar.goto(0);changeCover(a);ajaxLyric(a,lyricCallback);music_bar.lock(false)}function mBcallback(a){var b=rem.audio[0].duration*a;rem.audio[0].currentTime=b;refreshLyric(b)}function vBcallback(a){if(rem.audio[0]!==undefined){rem.audio[0].volume=a}if($(".btn-quiet").is('.btn-state-quiet')){$(".btn-quiet").removeClass("btn-state-quiet")}if(a===0)$(".btn-quiet").addClass("btn-state-quiet");playerSavedata('volume',a)}var initProgress=function(){music_bar=new mkpgb("#music-progress",0,mBcallback);music_bar.lock(true);var a=playerReaddata('volume');a=(a!=null)?a:(rem.isMobile?1:mkPlayer.volume);if(a<0)a=0;if(a>1)a=1;if(a==0)$(".btn-quiet").addClass("btn-state-quiet");volume_bar=new mkpgb("#volume-progress",a,vBcallback)};mkpgb=function(a,b,c){this.bar=a;this.percent=b;this.callback=c;this.locked=false;this.init()};mkpgb.prototype={init:function(){var b=this,mdown=false;$(b.bar).html('<div class="mkpgb-bar"></div><div class="mkpgb-cur"></div><div class="mkpgb-dot"></div>');b.minLength=$(b.bar).offset().left;b.maxLength=$(b.bar).width()+b.minLength;$(window).resize(function(){b.minLength=$(b.bar).offset().left;b.maxLength=$(b.bar).width()+b.minLength});$(b.bar+" .mkpgb-dot").mousedown(function(e){e.preventDefault()});$(b.bar).mousedown(function(e){if(!b.locked)mdown=true;barMove(e)});$("html").mousemove(function(e){barMove(e)});$("html").mouseup(function(e){mdown=false});function barMove(e){if(!mdown)return;var a=0;if(e.clientX<b.minLength){a=0}else if(e.clientX>b.maxLength){a=1}else{a=(e.clientX-b.minLength)/(b.maxLength-b.minLength)}b.callback(a);b.goto(a);return true}b.goto(b.percent);return true},goto:function(a){if(a>1)a=1;if(a<0)a=0;this.percent=a;$(this.bar+" .mkpgb-dot").css("left",(a*100)+"%");$(this.bar+" .mkpgb-cur").css("width",(a*100)+"%");return true},lock:function(a){if(a){this.locked=true;$(this.bar).addClass("mkpgb-locked")}else{this.locked=false;$(this.bar).removeClass("mkpgb-locked")}return true}};